mapper.select:



        and template_name like concat('%', #{templateName}, '%')


        and template_key = #{templateKey}


        and resource_type = #{resourceType}

    and status = #{status}

order by is_system desc, create_time desc

mapper.insert:
insert into sys_permission_template (
    template_name, template_key, resource_type, permission_config, description,
    is_system, status, create_by, create_time, remark
) values (
    #{templateName}, #{templateKey}, #{resourceType}, #{permissionConfig}, #{description},
    #{isSystem}, #{status}, #{createBy}, #{createTime}, #{remark}
)

mapper.update:
update sys_permission_template
template_name = #{templateName}, template_key = #{templateKey}, resource_type = #{resourceType}, permission_config = #{permissionConfig}, description = #{description}, is_system = #{isSystem}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
