mapper.select:


    and user_id = #{userId}

        and user_name like concat('%', #{userName}, '%')

    and resource_type = #{resourceType}
    and resource_id = #{resourceId}

        and resource_name like concat('%', #{resourceName}, '%')

    and action_type = #{actionType}
    and target_type = #{targetType}
    and target_id = #{targetId}
    and status = #{status}

        and create_time >= #{params.beginTime}


        and create_time <= #{params.endTime}


order by create_time desc

mapper.insert:
insert into sys_resource_permission_log (
    user_id, user_name, resource_type, resource_id, resource_name, action_type,
    target_type, target_id, target_name, permission_detail, ip_address, status, error_msg, create_time
) values (
    #{userId}, #{userName}, #{resourceType}, #{resourceId}, #{resourceName}, #{actionType},
    #{targetType}, #{targetId}, #{targetName}, #{permissionDetail}, #{ipAddress}, #{status}, #{errorMsg}, #{createTime}
)
