mapper.select:


    and p.user_id = #{userId}
    and p.instance_id = #{instanceId}
    and p.node_id = #{nodeId}
    and p.permission_type = #{permissionType}
    and p.status = #{status}

order by p.create_time desc

mapper.insert:
insert into sys_user_mc_instance (
    user_id, instance_id, node_id, permission_type, can_view, can_start, can_stop, can_restart,
    can_console, can_file, can_config, can_delete, console_cmd_whitelist, console_cmd_blacklist,
    file_path_whitelist, file_path_blacklist, expire_time, status, create_by, create_time, remark
) values (
    #{userId}, #{instanceId}, #{nodeId}, #{permissionType}, #{canView}, #{canStart}, #{canStop}, #{canRestart},
    #{canConsole}, #{canFile}, #{canConfig}, #{canDelete}, #{consoleCmdWhitelist}, #{consoleCmdBlacklist},
    #{filePathWhitelist}, #{filePathBlacklist}, #{expireTime}, #{status}, #{createBy}, #{createTime}, #{remark}
)

mapper.update:
update sys_user_mc_instance
permission_type = #{permissionType}, can_view = #{canView}, can_start = #{canStart}, can_stop = #{canStop}, can_restart = #{canRestart}, can_console = #{canConsole}, can_file = #{canFile}, can_config = #{canConfig}, can_delete = #{canDelete}, console_cmd_whitelist = #{consoleCmdWhitelist}, console_cmd_blacklist = #{consoleCmdBlacklist}, file_path_whitelist = #{filePathWhitelist}, file_path_blacklist = #{filePathBlacklist}, expire_time = #{expireTime}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
