mapper.select:


    and p.user_id = #{userId}
    and p.node_id = #{nodeId}
    and p.permission_type = #{permissionType}
    and p.status = #{status}

order by p.create_time desc

mapper.insert:
insert into sys_user_node_server (
    user_id, node_id, permission_type, can_view, can_operate, can_manage, can_create_instance,
    expire_time, status, create_by, create_time, remark
) values (
    #{userId}, #{nodeId}, #{permissionType}, #{canView}, #{canOperate}, #{canManage}, #{canCreateInstance},
    #{expireTime}, #{status}, #{createBy}, #{createTime}, #{remark}
)

mapper.update:
update sys_user_node_server
permission_type = #{permissionType}, can_view = #{canView}, can_operate = #{canOperate}, can_manage = #{canManage}, can_create_instance = #{canCreateInstance}, expire_time = #{expireTime}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
