mapper.select:


    and p.user_id = #{userId}
    and p.server_id = #{serverId}
    and p.permission_type = #{permissionType}
    and p.status = #{status}

order by p.create_time desc

mapper.insert:
insert into sys_user_rcon_server (
    user_id, server_id, permission_type, can_execute_cmd, can_view_log, can_manage,
    cmd_whitelist, cmd_blacklist, expire_time, status, create_by, create_time, remark
) values (
    #{userId}, #{serverId}, #{permissionType}, #{canExecuteCmd}, #{canViewLog}, #{canManage},
    #{cmdWhitelist}, #{cmdBlacklist}, #{expireTime}, #{status}, #{createBy}, #{createTime}, #{remark}
)

mapper.update:
update sys_user_rcon_server
permission_type = #{permissionType}, can_execute_cmd = #{canExecuteCmd}, can_view_log = #{canViewLog}, can_manage = #{canManage}, cmd_whitelist = #{cmdWhitelist}, cmd_blacklist = #{cmdBlacklist}, expire_time = #{expireTime}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
